/* Full Screen */
document.addEventListener("keydown", function(event) {if (event.ctrlKey && event.keyCode === 70) {Fs();}});

/* Dim */
document.addEventListener("keydown", function(event) {
  if (event.ctrlKey && event.key === "a" || event.key === "A") {
    document.addEventListener("keydown", function(event) {
      if ((event.ctrlKey && event.key === "p" || event.key === "P") && !event.ctrlKey) {
        dimScreen();
        toastNotif("<i class='check'></i><b>Activated</b>&nbsp;Dim Screen Mode");
      }
    });
  }
  
  if (event.ctrlKey && event.key === "d" || event.key === "D") {
    document.addEventListener("keydown", function(event) {
      if ((event.ctrlKey && event.key === "p" || event.key === "P") && !event.ctrlKey) {
        dimScreen();
        toastNotif("<i class='warn'></i><b>Deactivated</b>&nbsp;Dim Screen Mode");
      }
    });
  }
});
  
/* Music Player */
document.addEventListener("keydown", function(event) {
  if (event.ctrlKey && event.key === "m" || event.key === "M") {
    document.addEventListener("keydown", function(event) {
      if ((event.key === "p" || event.key === "P") && !event.ctrlKey) {
        window.location.href = "https://raghavendratechnic.blogspot.com/p/music_3.html";
      }
    });
  }
});
